package ioutils

import (
	"bytes"
	"encoding/base64"
	"encoding/binary"
	"github.com/stretchr/testify/assert"
	"testing"
)

// 128x83 transparent PNG
const png1 = ""

const (
	pngHeader = "\x89PNG\r\n\x1a\n"
	chunkIHDR = "IHDR"
	chunkPLTE = "PLTE"
	chunktRNS = "tRNS"
	chunkIDAT = "IDAT"
	chunkIEND = "IEND"
	sizeIHDR  = 13
)

type PNGHeader [8]byte

type Chunk struct {
	Size uint32
	Type [4]byte
}

type IHDR struct {
	Width       uint32
	Height      uint32
	Bit         uint8
	ColorType   uint8
	Compression uint8
	Filter      uint8
	Interlace   uint8
}

/**
 * IHDR chunk with named structs
 */
type ChunkIHDR struct {
	Header   Chunk
	Contents IHDR
	CRC      uint32
}

/**
 * Initial PNG struct with anonymous structs
 */
type PNG struct {
	PNGHeader
	Chunk
	IHDR
	CRC uint32
}

var validPNGHeader = PNGHeader{0x89, 0x50, 0x4e, 0x47, 0xd, 0xa, 0x1a, 0xa}

/**
 * Reads PNG header from base64 image with separate structs
 */
func TestReadPNGHeader(t *testing.T) {
	file, err := fromBase64(png1)
	assert.Nil(t, err)

	// Read header
	header := &PNGHeader{}
	err = ReadStruct(file, header, binary.BigEndian)
	assert.Nil(t, err)
	assert.Equal(t, &validPNGHeader, header)

	// Read IHDR Chunk
	ihdr := &ChunkIHDR{}
	err = ReadStruct(file, ihdr, binary.BigEndian)
	assert.Nil(t, err)
	if err == nil {
		// validate ihdr
		assert.Equal(t, chunkIHDR, string(ihdr.Header.Type[:]))
		assert.Equal(t, uint32(13), ihdr.Header.Size)
		assert.Equal(t, uint32(128), ihdr.Contents.Width)
		assert.Equal(t, uint32(83), ihdr.Contents.Height)
		assert.Equal(t, uint8(8), ihdr.Contents.Bit)
	}
}

/**
 * Reads PNG header from base64 image with anonymous structs
 */
func TestReadPNGHeader_Embedded(t *testing.T) {
	file, err := fromBase64(png1)
	assert.Nil(t, err)

	// Read PNG header with IHDR
	header := &PNG{}
	err = ReadStruct(file, header, binary.BigEndian)
	assert.Nil(t, err)
	if err == nil {
		// png signature
		assert.Equal(t, validPNGHeader, header.PNGHeader)
		// ihdr
		assert.Equal(t, chunkIHDR, string(header.Type[:]))
		assert.Equal(t, uint32(13), header.Size)
		assert.Equal(t, uint32(128), header.Width)
		assert.Equal(t, uint32(83), header.Height)
		assert.Equal(t, uint8(8), header.Bit)
	}
}


func fromBase64(b64 string) (*bytes.Buffer, error) {
	data, err := base64.StdEncoding.DecodeString(b64)
	if err == nil {
		return bytes.NewBuffer(data), nil
	}
	return nil, err
}
